import React , { useState, useEffect } from 'react';
import 'isomorphic-fetch';
import 'es6-promise';
import ReactBootstrap, {InputGroup,FormControl,Nav, Button, Navbar,NavDropdown,Table} from 'react-bootstrap';




function OrderMngTable() {

	

	const [title,setTitle] = useState(["Accept"]);

	
	

	const acceptOrder=(idd)=>{
		setData(myData.filter(d=>d.order_id!=idd))
		let obj = {idd,"action":"yes"}
		console.log(obj)
	}
	const rejectOrder=(idd)=>{
		setData(myData.filter(d=>d.order_id!=idd))
	}

	// https://stackoverflow.com/questions/56896037/using-react-hooks-axios-to-fetch-data-and-display-in-a-table		
	const renderTable = () => {
	 
	 return(
			  <tr>
		          <td>{user.date}</td>
		          <td>{user.order_id}</td>
		          <td>{user.cust_id}</td>
		          <td>{user.address}</td>
		          <td>{user.delivery_items}</td>
		          <td>{user.qty}</td>
		          <td>{user.total}</td>
		          <td>{user.order_type}</td>
		          <td><span onClick={()=>setTitle("Yoo")} className="bg-green">{title}</span><span onClick={()=>rejectOrder(user.order_id)} className="bg-red">Reject</span></td>
				  <td><span className="bg-gray">Preparing</span><span className="bg-light-silver">Done</span></td>
	        </tr>
	      )
// https://react-bootstrap.github.io/components/table/
	
      
    
	}

}

